mapper.select:
select c.key custom_key,l.id,l.custom_key remarks4,l.model_id, l.create_time, l.create_user, l.update_time, l.update_user,
l.remarks1, l.remarks2, l.remarks3, l.remarks4 remak, l.remarks5, l.remarks6, l.remarks7, l.remarks8, l.remarks9,
l.remarks10 from jintie_model_list l LEFT JOIN jintie_custom  c on l.custom_key = c.customName
where remarks3 = '已启用'

  and remarks2 = #{parm.type,jdbcType=VARCHAR}


  and remarks1 like concat('%',#{parm.name,jdbcType=VARCHAR},'%')


  and create_user = #{parm.user,jdbcType=VARCHAR}


  and c.key =  #{parm.custom,jdbcType=VARCHAR}



GROUP BY create_time desc
